import React, { Fragment } from 'react'
import './header.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { logout } from '../../redux/actions';
import { useAlert } from 'react-alert'

const Header = ({ isAuthenticated, logout }) => {
    const alert = useAlert()
    const handleLogoutClick = (e) => {
        // alert.show('Oh look, an alert!')
        e.preventDefault();
        logout();
    }
    let value = isAuthenticated
        ? (
            <li className="header-auth-list__element">
                <button onClick={handleLogoutClick}>Выйти</button>
            </li>
        )
        : (
            <Fragment>
                <li className="header-auth-list__element">
                    <Link to="/register">Регистрация</Link>
                </li>
                <li className="header-auth-list__element">
                    <Link to="/login">Войти</Link>
                </li>
                <li className="header-auth-list__element" onClick={handleLogoutClick}>
                    Войти
                </li>
            </Fragment>
        )
    return (
        <header className="header">
            <h1 className="header__element header__element--heading">
                <i className="fa fa-address-book" aria-hidden="true"></i>
                Книга контактов
            </h1>
            <ul className="header__element header-auth-list">
                {value}
            </ul>
        </header>
    )
}

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
})

export default connect(mapStateToProps, { logout })(Header)
