import {
    ADD_CONTACT_FAILURE,
    ADD_CONTACT_LOADING,
    ADD_CONTACT_SUCCESS,
    GET_CONTACTS_LOADING,
    GET_CONTACTS_SUCCESS,
    UPLOAD_CONTACT_ITEM,
    CLEAR_CONTACT_ITEM,
    DELETE_CONTACT_SUCCESS
} from '../action-types';

const initialState = {
    contactList: [],
    isLoading: false,
    showEdit: false,
    id: null
}

export default function contacts(state = initialState, action) {
    switch (action.type) {
        case GET_CONTACTS_LOADING:
            return {
                ...state,
                isLoading: true
            }
        case GET_CONTACTS_SUCCESS:
            return {
                ...state,
                contactList: action.payload,
                isLoading: false
            }
        case ADD_CONTACT_SUCCESS:
            return {
                ...state,
                contactList: [action.payload, ...state.contactList],
                isLoading: false
            }
        case UPLOAD_CONTACT_ITEM:

            return {
                ...state,
                showEdit: true,
                id: action.payload
            }
        case CLEAR_CONTACT_ITEM:

            return {
                ...state,
                showEdit: false,
                id: null
            }
        case DELETE_CONTACT_SUCCESS:
            return {
                ...state,
                showEdit: false,
                id: null
            }
        default:
            return state;
    }
};
