import {
    LOGIN_FAILURE,
    LOGIN_SUCCESS,
    LOGIN_FETCH,
    REGISTER_FAILURE,
    REGISTER_SUCCESS,
    REGISTER_FETCH,
    USER_LOADING,
    USER_LOADED,
    USER_FAILURE
} from '../redux/action-types';
const link = 'http://127.0.0.1:8000/api/accounts/';

export const getUser = () => (dispatch, getState) => {
    dispatch({
        type: USER_LOADING
    })

    const token = localStorage.getItem('token');

    if (!token) {
        return dispatch({
            type: USER_FAILURE
        })
    }
    fetch(`${link}user/`, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Token ${token}`,
        }
    })
        .then(res => {
            console.log(res)
            return res.json()
                .then((data) => {
                    if (res.ok) {
                        dispatch({
                            type: USER_LOADED,
                            payload: data
                        })
                    } else {
                        dispatch({
                            type: USER_FAILURE,
                            payload: data
                        })
                    }
                })
        })
        .then(data => { console.log(data) })
}

export const loginUser = (data) => (dispatch) => {
    dispatch({
        type: LOGIN_FETCH
    })
    fetch(`${link}login/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    })
        .then(res => {
            return res.json()
                .then((data) => {
                    if (res.ok) {
                        console.log(data)
                        dispatch({
                            type: LOGI,
                            payload: data
                        })
                    } else {
                        dispatch({
                            type: LOGIN_FAILURE,
                            payload: data
                        })
                    }
                })
        })
        .then(data => { console.log(data) })
}


export const register = (data) => dispatch => {
    dispatch({
        type: REGISTER_FETCH
    })
    fetch(`${link}register/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    })
        .then((res) => {
            return res.json()
                .then((data) => {
                    if (res.ok) {
                        dispatch({
                            type: REGISTER_SUCCESS,
                            payload: data
                        })
                    }
                })
        })
        .then(data => { console.log(data) })
}
