import {
    LOGIN_FAILURE,
    LOGIN_SUCCESS,
    LOGIN_FETCH,
    REGISTER_SUCCESS,
    REGISTER_FETCH,
    REGISTER_FAILURE
} from '../action-types';

const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: false,
    isLoading: false,
    error: null,
    user: null
}

export default function auth(state = initialState, action) {
    switch (action.type) {
        case LOGIN_FETCH:
        case REGISTER_FETCH:
            return {
                ...state,
                isLoading: true
            }
        case LOGIN_SUCCESS:
        case REGISTER_SUCCESS:
            return {
                ...state,
                isLoading: false,
                isAuthenticated: true,
                user: action.payload
            }
        case  LOGIN_FAILURE:
            return {
                ...state,
                isLoading: false,
                isAuthenticated: false,
                
            }
        default:
            return state;
    }
};
